mapper.select:
select 


from credit_adj_dt
where BillNO = #{billno,jdbcType=INTEGER}
  and EntId = #{entid,jdbcType=VARCHAR}
  and BillSn = #{billsn,jdbcType=INTEGER}

mapper.delete:
delete from credit_adj_dt
where BillNO = #{billno,jdbcType=INTEGER}
  and EntId = #{entid,jdbcType=VARCHAR}
  and BillSn = #{billsn,jdbcType=INTEGER}
